  `spd` float NOT NULL DEFAULT '0' COMMENT '机动性',
  `spd_up` float NOT NULL DEFAULT '0' COMMENT '机动性提升',
  `hp` float NOT NULL DEFAULT '0' COMMENT '生命值',
  `hp_up` float NOT NULL DEFAULT '0' COMMENT '生命值提升',
