CREATE TABLE `skill_effect` (
  `id` int(10) NOT NULL COMMENT 'ID',
  `effect_id` int(10) NOT NULL DEFAULT '0' COMMENT '效果ID',
  `lv` int(10) NOT NULL DEFAULT '0' COMMENT '效果等级',
  `effect_name` varchar(150) NOT NULL DEFAULT '0' COMMENT '[Language]效果名',
  `intro` varchar(200) NOT NULL DEFAULT '0' COMMENT '[Language]效果描述',
  `camp` int(10) NOT NULL DEFAULT '0' COMMENT '目标阵营。0：无视敌我。1我方。2敌方',
  `arng` int(10) NOT NULL DEFAULT '0' COMMENT '效果作用范围：0：技能选定目标。1：施法者。2技能指定点。3。触发者',
  `effect_type` int(10) NOT NULL DEFAULT '0' COMMENT '效果类型：1=伤害 2=治疗 3=护甲 4=耐力消耗 5=移速 6=自动瞄准 7=侦查 8=生成触发器（triggers） 9=射速 10=精准度 11=稳定性 12=射程 13=穿透 14=击退目标 15=暴击 16=弹匣容量 17=枪械填弹时间 18=生命 19=防爆 20=换弹加速 21=道具强度 22=补满弹夹 23=增加物品 24=技能等级 25=攻击力 26=负重 27=耐力上限 28=持续伤害 29=持续恢复 30=生成触发器群（skill_triggers） 31=伤害吸收盾 32=耐力值增减 33人物暴击 34=经验提升 35=全局攻击力 36=道具伤害 37=道具补血 38=改变技能CD时间 41=无敌 42=位移 39=补充子弹 40=沉默 43=眩晕 44=定身 45=睡眠 46=传送标记 47=传送 48=召唤NPC 49=抓钩50=移除生命51=上机甲 52=下机甲53=护甲穿透54=释放光环55=标记56=燃油57=火焰58=隐身 60=引力弹 67=复活光环，70=闪光弹 71=草丛，72=单次重生',
  `effect_var_type` int(10) NOT NULL DEFAULT '0' COMMENT '效果因子：0值，1百分比。',
  `effect_base` float NOT NULL DEFAULT '0' COMMENT '效果值',
  `effect_time` int(10) NOT NULL DEFAULT '0' COMMENT '效果持续时间.-1=永久存在',
  `effect_acttimes` int(10) NOT NULL DEFAULT '0' COMMENT '效果作用次数',
  `effect_canmuti` int(10) NOT NULL DEFAULT '0' COMMENT '是否可叠加',
  `hit_type` int(10) NOT NULL DEFAULT '0' COMMENT '0、不生效，1、轻度受击，2、重度受击，取常量表',
  `interrupt` int(10) NOT NULL DEFAULT '0' COMMENT '索引到skill_effect_interrupt表',
  `special_type` int(10) NOT NULL DEFAULT '0' COMMENT 'effect1：1=手雷爆炸，中心最高，边缘递减  effect 54：1=翻滚附带一次性触发光环2=随动光环重置3=随动光环远离消失4.光环时间触发',
  `special_type_param` varchar(100) NOT NULL DEFAULT '0' COMMENT '特殊伤害机制参数（effect54表示光环范围）',
  `delay_time` int(10) NOT NULL DEFAULT '0' COMMENT '效果生效延迟时间。0表示不延迟，立即生效。单位毫秒',
  PRIMARY KEY (`effect_id`,`lv`),
  UNIQUE KEY `id` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='[logic server no use]';
