DROP TABLE IF EXISTS `talent`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `talent` (
  `talent_id` int(10) NOT NULL DEFAULT '0' COMMENT '天赋id',
  `hero_id` int(10) NOT NULL DEFAULT '0' COMMENT '英雄id（英雄id为0时，表示战斗内成长的）',
  `img_id` int(10) NOT NULL DEFAULT '0' COMMENT '天赋图标',
  `name` char(50) NOT NULL DEFAULT '0' COMMENT '[Language]天赋名称',
  `intro` char(200) NOT NULL DEFAULT '0' COMMENT '[Language]天赋描述',
  `max_lv` int(10) NOT NULL DEFAULT '0' COMMENT '最大等级上限',
  `talent_type` int(10) NOT NULL DEFAULT '0' COMMENT '天赋起作用时所针对的对象类型： 1:步枪2:机枪3：狙击4：散弹0:通用',
  `effect1_id` int(10) NOT NULL DEFAULT '0' COMMENT '效果id',
  `effect2_id` int(10) NOT NULL DEFAULT '0' COMMENT '效果id2',
  `trigger_type` int(10) NOT NULL DEFAULT '0' COMMENT '触发的条件类型：0常驻、1翻滚后、2丢出手雷后、3被击中、4击杀后、5命中后、6生命低于XX后、7使用技能后。8、拾取经验后。9=开枪后。10=技能击杀后。11=技能命中后。12=时间触发。13=死亡后触发14=多少时间不受伤触发',
  `trigger_parm` varchar(50) NOT NULL DEFAULT '0' COMMENT '触发的条件参数.多个等级如果不同则以;隔开5;3;1表示1级5ms;2级3ms;3级1ms。如果只有一个值，则表示3级一样。同一级别内多个内容用:隔开。表示符合要求的参数。',
  `trigger_cd` varchar(50) NOT NULL DEFAULT '0' COMMENT '触发的冷却时间.多个等级如果不同则以;隔开5;3;1表示1级5ms;2级3ms;3级1ms。如果只有一个值，则表示3级一样。',
  `trigger_chance` varchar(50) NOT NULL DEFAULT '0' COMMENT '触发概率。多个等级如果不同则以;隔开，如：5;3;1表示1级5%;2级3%;3级1%如果只有一个值，则表示3级一样。',
  `target_camp` int(10) NOT NULL DEFAULT '0' COMMENT '目标阵营。1我方。2敌方',
  `trigger_num` int(10) NOT NULL DEFAULT '0' COMMENT '触发次数',
  `trigger_effect_type` int(10) NOT NULL DEFAULT '0' COMMENT '触发效果类型：1、以自己为中心的范围AOE',
  `trigger_effect_param` int(10) NOT NULL DEFAULT '0' COMMENT '触发效果类型1：表示range半径',
  `trigger_reset_time` int(10) NOT NULL DEFAULT '0' COMMENT '触发重置时间。用以做复活时间',
  PRIMARY KEY (`talent_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='[logic server no use]';
